import React from 'react';
// import { Redirect, history } from 'react-router';
import { BrowserRouter as Router, Route, Switch, Link, Redirect } from 'react-router-dom';
import { Home, About, Projects } from './pages/index';
// import { createBrowserHistory } from "history";
import './App.css';

class App extends React.Component {
  render(){

    return(

      <div>
        <Router>
          <nav id="homebodynav">
            <div className="name"><span>Neo Owens</span></div>
              <ul className="navlist" id="pNavList">
                <li><Link className="nav-link" to="/home">Home</Link></li>
                <li><Link className="nav-link" to="/projects">Projects</Link></li>
                <li><Link className="nav-link" to="/about">About Me</Link></li>
              </ul>
          </nav>

          <Switch>
            <Route exact path="/" component = {Home}><Redirect to="/home"></Redirect></Route>
            <Route path="/about" component = {About} />
            <Route path="/projects" component = {Projects} />
          </Switch>
        </Router>
      </div>
    )
  }
}

export default App;
